package com.francotte.add_recipeimport android.net.Uriimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.navigation.NavControllerimport androidx.navigation.NavGraphBuilderimport androidx.navigation.NavOptionsimport androidx.navigation.compose.composableimport com.francotte.common.ScreenCounterimport com.francotte.network.model.Ingredientconst val ADD_ROUTE = "add_route"fun NavController.navigateToAddRecipeScreen(navOptions: NavOptions? = null) {    this.navigate(ADD_ROUTE, navOptions)}fun NavGraphBuilder.addRecipeScreen(    isAuthenticated: Boolean,    goToLoginScreen: () -> Unit,    onSubmit: (        title: String,        ingredients: List<Ingredient>,        instructions: String, image: Uri?    ) -> Unit) {    composable(route = ADD_ROUTE) {        AddRoute(isAuthenticated,{ goToLoginScreen()}, onSubmit)    }}@Composablefun AddRoute(    isAuthenticated: Boolean, goToLoginScreen: () -> Unit, onSubmit: (        title: String,        ingredients: List<Ingredient>,        instructions: String, image: Uri?    ) -> Unit) {    AddRecipeScreen(        isAuthenticated = isAuthenticated,        goToLoginScreen = { goToLoginScreen() },        onSubmit = onSubmit    )    LaunchedEffect(Unit) { ScreenCounter.increment() }}