package com.francotte.add_recipeimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.navigation.NavControllerimport androidx.navigation.NavGraphBuilderimport androidx.navigation.NavOptionsimport androidx.navigation.compose.composableimport com.francotte.common.counters.ScreenCounterconst val ADD_ROUTE = "add_route"fun NavController.navigateToAddRecipeScreen(navOptions: NavOptions? = null) {    this.navigate(ADD_ROUTE, navOptions)}fun NavGraphBuilder.addRecipeScreen(    isAuthenticated: Boolean,    goToLoginScreen: () -> Unit) {    composable(route = ADD_ROUTE) {        AddRoute(isAuthenticated,{ goToLoginScreen()})    }}@Composablefun AddRoute(    isAuthenticated: Boolean, goToLoginScreen: () -> Unit) {    AddRecipeScreen(        isAuthenticated = isAuthenticated,        goToLoginScreen = { goToLoginScreen() }    )    LaunchedEffect(Unit) { ScreenCounter.increment() }}